import { createRequire as topLevelCreateRequire } from 'module'
const require = topLevelCreateRequire(import.meta.url)

// logs/index.ts
import assert from "assert";
var multiples = {
  day: 1,
  days: 1,
  week: 7,
  weeks: 7,
  year: 365
};
var defaultRetention = 7;
var customRetention;
var logs = {
  get retention() {
    return customRetention || defaultRetention;
  },
  retain(quantity, unit = "days") {
    const num = Number(quantity);
    assert(Number.isFinite(num), "logs retention must be a finite number");
    assert(unit in multiples, 'logs retention unit must be "days" or "weeks"');
    customRetention = num * multiples[unit];
    assert(customRetention >= 1, "logs retention minimum is 1 day");
    assert(customRetention <= 365, "logs retention maximum is 1 year");
  }
};

export {
  logs
};
